import React from "react";

class Users extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            users: [
                {
                  id: 1,
                  firstname: "John",
                  lastname: "Smith",
                  bio: "John Smith",
                  age: 40,
                  isHappy: true,
                },
                {
                  id: 2,
                  firstname: "George",
                  lastname: "House",
                  bio: "George House",
                  age: 22,
                  isHappy: false,
                },
                {
                  id: 3,
                  firstname: "Peter",
                  lastname: "London",
                  bio: "Peter Library",
                  age: 33,
                  isHappy: true,
                },
              ]
        }
    }

  render() {
    if (this.state.users.length > 0) {
        return (
            <div>
              {this.users.map((el) => (
                <div className="user" key={el.id}>
                  <h3>
                    {el.firstname} {el.lastname}
                  </h3>
                  <p>{el.bio}</p>
                  <b>{el.isHappy ? "Happy :)" : "Not enough :("}</b>
                </div>
              ))}
            </div>
          );
    } else {
        return (
            <div className="user">
                <h3>Users empty</h3>
            </div>
          );
    }

  }
}

export default Users;
