import React from "react";
import Header from "./components/Header";
import Users from "./components/Users";
import AddUser from "./components/AddUser";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      users: [
        {
          id: 1,
          firstname: "John",
          lastname: "Smith",
          bio: "John Smith",
          age: 40,
          isHappy: true,
        },
        {
          id: 2,
          firstname: "George",
          lastname: "House",
          bio: "George House",
          age: 22,
          isHappy: false,
        },
        {
          id: 3,
          firstname: "Peter",
          lastname: "London",
          bio: "Peter Library",
          age: 33,
          isHappy: true,
        },
      ],
    };
  }

  render() {
    return (
      <div>
        <Header title="List of users" />
        <main>
          <Users users={this.state.users} />
        </main>
        <aside>
          <AddUser onAdd={this.addUser} />
        </aside>
      </div>
    );
  }

  addUser(user) {
    console.log(user);
  }
}

export default App;
