import React from "react";
import {IoCloseCircleSharp} from "react-icons/io5"
import {AiTwotoneEdit} from "react-icons/ai"

class User extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
          users: [
            {
              id: 1,
              firstname: "John",
              lastname: "Smith",
              bio: "John Smith",
              age: 40,
              isHappy: true,
            },
            {
              id: 2,
              firstname: "George",
              lastname: "House",
              bio: "George House",
              age: 22,
              isHappy: false,
            },
            {
              id: 3,
              firstname: "Peter",
              lastname: "London",
              bio: "Peter Library",
              age: 33,
              isHappy: true,
            },
          ],
        };
      }

  user = this.props.user;

  render() {
    return (
      <div className="user">
        <IoCloseCircleSharp className="delete-icon"/>
        <AiTwotoneEdit className="edit-icon"/>
        <h3>
          {this.user.firstname} {this.user.lastname}
        </h3>
        <p>{this.user.bio}</p>
        <b>{this.user.isHappy ? "Happy :)" : "Not enough :("}</b>
      </div>
    );
  }
}

export default User;
