import React from "react";

class AddUser extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstname: "",
      lastname: "",
      bio: "",
      age: 1,
      isHappy: false,
    };
  }

  render() {
    return (
      <form>
        <input
          placeholder="Firsname"
          onChange={(e) => this.setState({ firstname: e.target.value })}
        />
        <input
          placeholder="Lastname"
          onChange={(e) => this.setState({ lastname: e.target.value })}
        />
        <textarea
          placeholder="Bio"
          onChange={(e) => this.setState({ bio: e.target.value })}
        />
        <input
          placeholder="Age"
          onChange={(e) => this.setState({ age: e.target.value })}
        />
        <label htmlFor="isHappy">Is happy?</label>
        <input
          type="checkbox"
          id="isHappy"
          onChange={(e) => this.setState({ isHappy: e.target.checked })}
        />
        <button type="button" onClick={() => this.props.onAdd({
            firstname: this.target.firstname,
            lastname: this.target.lastname,
            bio: this.target.bio,
            age: this.target.age,
            isHappy: this.target.isHappy,
        })}>Add</button>
      </form>
    );
  }
}

export default AddUser;
